import React, { useState } from 'react';
import axios from 'axios';
import { load } from "@cashfreepayments/cashfree-js";
import { Cashfree } from "cashfree-pg"; 

Cashfree.XClientId = "7008519896fc1d4cef3ba0670d158007";
Cashfree.XClientSecret = "cfsk_ma_prod_5b73916dbcd9cbe497c30e9f27ad7b5f_7987c49a";
Cashfree.XEnvironment = Cashfree.Environment.PRODUCTION;

var request = {
    "order_amount": 1.00,
    "order_currency": "INR",
    "order_id": "order_23031138",
    "customer_details": {
        "customer_id": "walterwNrcMi",
        "customer_phone": "8474090589"
    },
    "order_meta": {
        "return_url": "https://www.cashfree.com/devstudio/preview/pg/web/checkout?order_id={order_id}"
    }
};

Cashfree.PGCreateOrder("2023-08-01", request).then((response) => {
    console.log('Order created successfully:',response.data);
}).catch((error) => {
    console.error('Error:', error.response.data.message);
});
function Checkout() {
    const [paymentSessionId, setPaymentSessionId] = useState(null);

    const initializeSDK = async () => {
        const cashfree = await load({
            mode: "production", // Use "sandbox" for sandbox environment
        });
        return cashfree;
    };

    const createOrder = async () => {
        try {
            console.log("Creating order..."); // Debug message
            const response = await axios.post('http://localhost:3001/create-order'); // Call your server endpoint
            console.log("Response from server:", response.data); // Debug message
            const { paymentSessionId } = response.data;
            console.log("Order created, session ID:", paymentSessionId); // Debug message
            setPaymentSessionId(paymentSessionId);
            return paymentSessionId;
        } catch (error) {
            console.error('Error creating order:', error.response ? error.response.data.message : error.message);
        }
    };

    const doPayment = async () => {
        const sessionId = await createOrder();
        if (!sessionId) {
            console.error('Payment session ID not set');
            return;
        }

        const cashfree = await initializeSDK();
        const checkoutOptions = {
            paymentSessionId: sessionId,
            redirectTarget: "_self",
        };
        cashfree.checkout(checkoutOptions);
    };

    return (
        <div className="row">
            <p>Click below to create an order and open the checkout page in the current tab</p>
            <button type="submit" className="btn btn-primary" id="renderBtn" onClick={doPayment}>
                Pay Now
            </button>
        </div>
    );
}

export default Checkout;
