import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Userstate from './userstate.js';
import { useNavigate } from 'react-router-dom';
 
import BadWordsFilter from 'badwords-list';

const Pay = () => {
  const [Textarea, setTextarea] = useState('');
  const [Mobile, setMobile] = useState('');
  const [password, setPassword] = useState('');
  const [image, setImage] = useState(null);
  const [userdata, setUserData] = useState([]);
  const [editMode, setEditMode] = useState(false);
  const [editUserId, setEditUserId] = useState(null);
  const [duration, setDuration] = useState('');
  const [previewData, setPreviewData] = useState(null);
  const [setSearchResult] = useState(null);
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();
  const [formatter] = useState(new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'INR',
    minimumFractionDigits: 0,
  }));
  const [isTextMode, setIsTextMode] = useState(false);
  const [isImageMode, setIsImageMode] = useState(false);

  const {
    states,
    state,
    district,
    category,
    categories,
    handleStateChange,
    handleDistrictChange,
    handleCategoryChange,
  } = Userstate();

  const durationPrices = {
    '2592000': 99,
    '5184000': 179,
    '15552000': 549,
    '31536000': 999,
  };

  useEffect(() => {
    fetchUsers();
    const interval = setInterval(() => {
      const currentDate = new Date();
      userdata.forEach(async (user) => {
        const userExpiryDate = new Date(user.createdAt).getTime() + user.duration;
        if (userExpiryDate <= currentDate.getTime()) {
          await handleDelete(user.ID);
        }
      });
    }, 1000);

    return () => clearInterval(interval);
  }, [userdata]);

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:8084');
      setUserData(filterNullValues(response.data));
    } catch (err) {
      console.error(err);
    }
  };

  const filterNullValues = (data) => {
    return data.map(post => {
      const filteredPost = {};
      for (const key in post) {
        if (post[key] !== '' && post[key] !== 0) {
          filteredPost[key] = post[key];
        }
      }
      return filteredPost;
    });
  };

  const loadScript = (src) => {
    return new Promise((resolve) => {
      const script = document.createElement('script');
      script.src = src;

      script.onload = () => {
        resolve(true);
      };

      script.onerror = () => {
        resolve(false);
      };

      document.body.appendChild(script);
    });
  };

  const displayRazorpay = async (amount, isImageUpload) => {
    if (!Mobile || !password) {
      alert('Please fill in Mobile, and Password');
      return;
    }
    if (Mobile.length !== 10) {
      alert('Mobile number must be exactly 10 digits');
      return;
    }
    if (password.length !== 6) {
      alert('Password must be exactly 6 digits');
      return;
    }
  
    const res = await loadScript('https://checkout.razorpay.com/v1/checkout.js');
  
    if (!res) {
      alert('You are offline... Failed to load Razorpay SDK');
      return;
    }
  
    const options = {
      key: 'rzp_test_n7o7FtkWD4wV92',
      currency: 'INR',
      amount: amount * 100,
      name: 'nearby',
      description: 'Thanks for purchasing',
      image: 'https://images.app.goo.gl/mCnzZoJ2AYPvYK6N6',
  
      handler: async function (response) {
        const formData = new FormData();
        formData.append('Textarea', Textarea);
        if (isImageUpload && image) {
          formData.append('image', image);
        }
        formData.append('Mobile', Mobile);
        formData.append('password', password);
        formData.append('State', state);
        formData.append('District', district);
        formData.append('Category', category);
        formData.append('duration', duration * 1000);
        formData.append('createdAt', new Date());
  
        try {
          await axios.post('http://localhost:8084/userdata', formData, {
            headers: { 'Content-Type': 'multipart/form-data' }
          });
          alert(`Payment Successful! Transaction ID: ${response.razorpay_payment_id}`);
          alert("your ad was created");
          fetchUsers();
          resetForm();
        } catch (err) {
          console.error(err);
        }
      },
      prefill: {
        name: 'nearby',
      },
    };
  
    const paymentObject = new window.Razorpay(options);
    paymentObject.open();
  };

  const checkForBadWords = (input) => {
    const filter = new BadWordsFilter();
    const hasBadWords = filter.isProfane(input);
    return hasBadWords;
  };

  const handleTextMobileSubmit = async (e) => {
    e.preventDefault();
    if (!Textarea || !Mobile || !password) {
      alert('Please fill in Textarea, Mobile, and Password');
      return;
    }

    const hasBadWords = checkForBadWords(Textarea);
    if (hasBadWords) {
      alert('Your input contains inappropriate words. Please modify your text.');
      return;
    }
    
    const formData = new FormData();
    formData.append('Textarea', Textarea);
    formData.append('Mobile', Mobile);
    formData.append('password', password);
    formData.append('State', state);
    formData.append('District', district);
    formData.append('Category', category);
    formData.append('duration', duration * 1000);
    

    setIsTextMode(true); 
    setIsImageMode(false);
    if (editMode) {
        try {
          await axios.put(`http://localhost:8084/userdata/${editUserId}`, formData, {
            headers: { 'Content-Type': 'multipart/form-data' }
          });
          alert('Data updated successfully');
          fetchUsers();
          resetForm();
        } catch (err) {
          console.error(err);
        }
      } else {
        displayRazorpay(durationPrices[duration], formData, false);
      }
    
  };

  const handleImageUpload = async (e) => {
    e.preventDefault();
    if (!image) {
      alert('Please select an image to upload');
      return;
    }
    if (!Mobile || !password) {
      alert('Please fill in  Mobile, and Password');
      return;
    }

    const hasBadWords = checkForBadWords(Textarea);
    if (hasBadWords) {
      alert('Your input contains inappropriate words. Please modify your text.');
      return;
    }
    
    const formData = new FormData();
    formData.append('image', image);
    formData.append('Mobile', Mobile);
    formData.append('password', password);
    formData.append('State', state);
    formData.append('District', district);
    formData.append('Category', category);
    formData.append('duration', duration * 1000); // Convert duration to milliseconds
    formData.append('createdAt', new Date());
    
    setIsTextMode(false);
    setIsImageMode(true);
    if (editMode) {
        try {
          await axios.put(`http://localhost:8084/userdata/${editUserId}`, formData, {
            headers: { 'Content-Type': 'multipart/form-data' }
          });
          alert('Data updated successfully');
          fetchUsers();
          resetForm();
        } catch (err) {
          console.error(err);
        }
      } else {
        displayRazorpay(durationPrices[duration], formData, true);
      }
  };
 
  const handleEdit = (userdata) => {
    setEditMode(true);
    setEditUserId(userdata.ID);
    setTextarea(userdata.Textarea);
    setMobile(userdata.Mobile);
    setPassword('');
    setPosts([]); // Clear the password input
  };
 
  const handleDelete = async (userId) => {
    try {
      await axios.delete(`http://localhost:8084/userdata/${userId}`);
      fetchUsers();
      setPosts([]);
    } catch (err) {
      console.error(err);
    }
  };
 
  const handlePreview = () => {
    if (!Textarea || !Mobile || !state || !district || !category) {
      alert('Please fill in all fields to preview');
      return;
    }
 
    const hasBadWords = checkForBadWords(Textarea);
    if (hasBadWords) {
      alert('Your input contains inappropriate words. Please modify your text.');
      return;
    }
 
    setPreviewData({
      Textarea,
      Mobile,
      State: state,
      District: district,
      Category: category,
    });
    setIsTextMode(true); // Set text mode to true
    setIsImageMode(false);
    setPosts(null);
  };
 
  const fetchPosts = async (searchParams) => {
    try {
      const response = await axios.get('http://localhost:8084/search', { params: searchParams });
      setPosts(filterNullValues(response.data));
    } catch (err) {
      console.error(err);
    }
  };
 
  const handleSearch = () => {
    const searchParams = { Mobile: Mobile, password: password };
    fetchPosts(searchParams);
    setPreviewData(null);
    setIsTextMode(false); // Set text mode to false
    setIsImageMode(false);
  };
 
  const resetForm = () => {
    setTextarea('');
    setMobile('');
    setPassword('');
    handleStateChange({ target: { value: '' } });
    handleDistrictChange({ target: { value: '' } });
    handleCategoryChange({ target: { value: '' } });
    setDuration('');
    setEditMode(false);
    setEditUserId(null);
    setSearchResult(null);
    setIsTextMode(false); // Reset text mode
    setIsImageMode(false);
  };
 
  const handleImageDelete = async (userId) => {
    try {
      await axios.delete(`http://localhost:8084/userdata/${userId}`);
      fetchUsers();
      setPosts([]);
    } catch (err) {
      console.error(err);
    }
  };
 
 

  return (
    <div>
    <button
        className="btn btn-primary mt-2 bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600"
        onClick={() => navigate('/')}
      >
        Home
      </button>
      <h2>User Details Form</h2>
      <form onSubmit={handleTextMobileSubmit}>
      <label htmlFor="Textarea">Textarea: </label>
      <textarea
    type="text"
    id="Textarea"
    name="Textarea"
    value={Textarea}
    onChange={(e) => setTextarea(e.target.value)}
  />
  <label htmlFor="Mobile">Mobile: </label>
        <input
          type="text"
          id="Mobile"
    name="Mobile"
          value={Mobile}
          onChange={(e) => setMobile(e.target.value)}
          placeholder="Enter mobile number"
        />
        <label htmlFor="password">Password: </label>
        <input
          type="text"
          id="password"
    name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Create your 6 digit password"
          required
        />
        {!editMode && (
          <>
            <select value={state} onChange={handleStateChange}>
              <option value="">Select State</option>
              {states.map((option) => (
                <option key={option.name} value={option.name}>
                  {option.name}
                </option>
              ))}
            </select>
            <select value={district} onChange={handleDistrictChange}>
              <option value="">Select District</option>
              {states
                .find((s) => s.name === state)?.districts?.map((districtOption) => (
                  <option key={districtOption} value={districtOption}>
                    {districtOption}
                  </option>
                ))}
            </select>
            <select value={category} onChange={handleCategoryChange}>
              <option value="">Select Category</option>
              {categories.map((option) => (
                <option key={option} value={option}>
                  {option}
                </option>
              ))}
            </select>
            <select value={duration} onChange={(e) => setDuration(parseInt(e.target.value))}>
              <option value="">Select Duration</option>
              <option value={30}>30 seconds</option>
              <option value={90}>90 seconds</option>
              <option value={2592000}>1 month</option>
              <option value={5184000}>2 months</option>
              <option value={15552000}>6 months</option>
              <option value={31536000}>12 months</option> required
            </select>
          </>
        )}
        <button type="submit">{editMode ? 'Update' : `Pay $${durationPrices[duration] || 0}`}</button>
      </form>
  
      <h3>Image Upload</h3>
      <form onSubmit={handleImageUpload}>
        <input
          type="file"
          onChange={(e) => setImage(e.target.files[0])}
        />
        {!editMode && (
          <>
            <input
              type="text"
              value={Mobile}
              onChange={(e) => setMobile(e.target.value)}
              placeholder="Enter mobile number"
            />
            <input
              type="text"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Create your 6 digit password"
              required
            />
            <select value={state} onChange={handleStateChange}>
              <option value="">Select State</option>
              {states.map((option) => (
                <option key={option.name} value={option.name}>
                  {option.name}
                </option>
              ))}
            </select>
            <select value={district} onChange={handleDistrictChange}>
              <option value="">Select District</option>
              {states
                .find((s) => s.name === state)?.districts?.map((districtOption) => (
                  <option key={districtOption} value={districtOption}>
                    {districtOption}
                  </option>
                ))}
            </select>
            <select value={category} onChange={handleCategoryChange}>
              <option value="">Select Category</option>
              {categories.map((option) => (
                <option key={option} value={option}>
                  {option}
                </option>
              ))}
            </select>
            <select value={duration} onChange={(e) => setDuration(parseInt(e.target.value))}>
              <option value="">Select Duration</option>
              <option value={30}>30 seconds</option>
              <option value={90}>90 seconds</option>
              <option value={2592000}>1 month</option>
              <option value={5184000}>2 months</option>
              <option value={15552000}>6 months</option>
              <option value={31536000}>12 months</option>
            </select>
          </>
        )}
        <button type="submit">Pay ${durationPrices[duration] || 0}</button>
      </form>
  
      <h2>Posted Details</h2>
      {userdata.map((user) => (
      <div key={user.ID}>
        {user.Image && user.Mobile ? (
          <>
            <img
              src={`http://localhost:8084/uploads/${user.Image}`}
              alt=""
              width="150px"
              height="200px"
            />  <br />
            <button onClick={() => handleImageDelete(user.ID)}>Delete Image</button>
          </>
        ) : (
          <>
            <p>{user.Textarea}</p>
            <p>{user.Mobile}</p>
            <button onClick={() => handleEdit(user)}>Edit</button>
            <button onClick={() => handleDelete(user.ID)}>Delete</button>
          </>
        )}
      </div>
    ))}
    </div>
  );
  
};

export default Pay;
